-- Seed Exercise Data: ~62 exercises across 6 categories
-- This populates the exercises table created in Phase 1

-- Get category IDs for reference
DO $$
DECLARE
  cardio_id UUID;
  strength_id UUID;
  flexibility_id UUID;
  core_id UUID;
  balance_id UUID;
  endurance_id UUID;
BEGIN
  SELECT id INTO cardio_id FROM exercise_categories WHERE name = 'Cardio';
  SELECT id INTO strength_id FROM exercise_categories WHERE name = 'Strength';
  SELECT id INTO flexibility_id FROM exercise_categories WHERE name = 'Flexibility';
  SELECT id INTO core_id FROM exercise_categories WHERE name = 'Core';
  SELECT id INTO balance_id FROM exercise_categories WHERE name = 'Balance';
  SELECT id INTO endurance_id FROM exercise_categories WHERE name = 'Endurance';

  -- CARDIO EXERCISES (10 exercises)

  -- STRENGTH EXERCISES (11 exercises)

  -- FLEXIBILITY EXERCISES (10 exercises)

  -- CORE EXERCISES (11 exercises)

  -- BALANCE EXERCISES (10 exercises)

  -- ENDURANCE EXERCISES (10 exercises)

END $$;