  INSERT INTO exercises (name, category_id, difficulty_level, duration_seconds, description, instructions, benefits, modifications, cautions, tier_required, display_order) VALUES
  ('Jumping Jacks', cardio_id, 1, 30, 'Classic full-body cardio exercise', 'Stand with feet together. Jump while spreading legs and raising arms overhead. Return to start.', ARRAY['Increases heart rate', 'Burns calories', 'Improves coordination'], ARRAY['Step side to side instead of jumping', 'Remove arm movement'], ARRAY['Avoid if you have knee or ankle issues'], 'free', 1),
  ('High Knees', cardio_id, 2, 45, 'Running in place with high knee lifts', 'Run in place, lifting knees to hip height with each step. Pump arms naturally.', ARRAY['Strengthens hip flexors', 'Boosts cardiovascular fitness', 'Improves running form'], ARRAY['March in place with lower knee lifts', 'Hold onto a chair for balance'], ARRAY['Use caution with knee problems'], 'free', 2),
  ('Butt Kicks', cardio_id, 2, 45, 'Running in place kicking heels to glutes', 'Jog in place, kicking heels up toward your glutes with each step.', ARRAY['Activates hamstrings', 'Warms up leg muscles', 'Improves agility'], ARRAY['Slow down the pace', 'Reduce range of motion'], ARRAY['Avoid if you have hamstring strains'], 'free', 3),
  ('Mountain Climbers', cardio_id, 3, 60, 'Dynamic plank variation with alternating knee drives', 'Start in plank position. Quickly alternate bringing knees toward chest.', ARRAY['Full-body workout', 'Core activation', 'Cardiovascular conditioning'], ARRAY['Slow down the pace', 'Place hands on elevated surface'], ARRAY['Not recommended for wrist pain'], 'free', 4),
  ('Burpees', cardio_id, 4, 60, 'Full-body explosive movement combining squat, plank, and jump', 'Squat down, place hands on floor, jump feet back to plank, return feet, jump up.', ARRAY['Burns maximum calories', 'Builds explosive power', 'Full-body conditioning'], ARRAY['Remove the jump', 'Step back instead of jumping', 'Skip the push-up'], ARRAY['High impact - use caution with joint issues'], 'free', 5),
  ('Jump Rope (No Rope)', cardio_id, 2, 60, 'Simulated jump rope movements', 'Jump continuously while rotating wrists as if holding a rope.', ARRAY['Improves coordination', 'Builds calf strength', 'Excellent cardio'], ARRAY['Do single bounces instead of double-unders', 'March with arm rotations'], ARRAY['Avoid with ankle or knee pain'], 'premium', 6),
  ('Speed Skaters', cardio_id, 3, 60, 'Side-to-side jumping movements', 'Leap side to side, landing on one foot while sweeping the other behind.', ARRAY['Lateral movement training', 'Balance improvement', 'Leg strength'], ARRAY['Step side to side instead of jumping', 'Reduce range of motion'], ARRAY['Use caution on slippery surfaces'], 'premium', 7),
  ('Sprint in Place', cardio_id, 4, 30, 'Maximum effort running in place', 'Run in place as fast as possible with high knees and pumping arms.', ARRAY['Builds explosive power', 'Maximum calorie burn', 'Improves speed'], ARRAY['Reduce intensity', 'Focus on form over speed'], ARRAY['High intensity - warm up first'], 'premium', 8),
  ('Lateral Shuffles', cardio_id, 2, 60, 'Side-to-side shuffling movements', 'Shuffle laterally, keeping knees bent and chest up. Touch the floor at each end.', ARRAY['Agility training', 'Lateral strength', 'Hip mobility'], ARRAY['Reduce range of motion', 'Remove floor touches'], ARRAY['Watch for knee alignment'], 'free', 9),
  ('Box Steps', cardio_id, 1, 60, 'Stepping up and down pattern', 'Step up and down in a rhythmic pattern: up-up-down-down.', ARRAY['Low-impact cardio', 'Coordination training', 'Leg endurance'], ARRAY['Use a lower step height', 'Slow down the pace'], ARRAY['Use stable surface only'], 'free', 10);
  INSERT INTO exercises (name, category_id, difficulty_level, duration_seconds, description, instructions, benefits, modifications, cautions, tier_required, display_order) VALUES
  ('Push-Ups', strength_id, 2, 30, 'Classic upper body strength exercise', 'Start in plank. Lower body until chest nearly touches floor. Push back up.', ARRAY['Builds chest strength', 'Strengthens triceps', 'Core activation'], ARRAY['Knee push-ups', 'Wall push-ups', 'Incline push-ups'], ARRAY['Maintain neutral spine throughout'], 'free', 1),
  ('Bodyweight Squats', strength_id, 1, 45, 'Fundamental lower body exercise', 'Stand with feet shoulder-width apart. Lower hips back and down. Return to standing.', ARRAY['Strengthens legs', 'Improves mobility', 'Functional movement'], ARRAY['Hold onto support', 'Reduce depth'], ARRAY['Keep knees tracking over toes'], 'free', 2),
  ('Lunges', strength_id, 2, 60, 'Single-leg strength and balance exercise', 'Step forward with one leg, lowering hips until both knees are at 90 degrees. Push back.', ARRAY['Leg strength', 'Balance training', 'Hip flexibility'], ARRAY['Static lunges', 'Shorter range'], ARRAY['Keep front knee over ankle'], 'free', 3),
  ('Tricep Dips', strength_id, 3, 30, 'Bodyweight tricep exercise using chair or bench', 'Sit on edge of chair, hands beside hips. Slide forward, lower body, push back up.', ARRAY['Tricep strength', 'Shoulder stability', 'Upper body endurance'], ARRAY['Keep feet closer', 'Reduce range of motion'], ARRAY['Avoid if you have shoulder pain'], 'free', 4),
  ('Wall Sit', strength_id, 2, 45, 'Isometric quad strengthening exercise', 'Lean back against wall with knees at 90 degrees. Hold position.', ARRAY['Quadriceps endurance', 'Isometric strength', 'Mental toughness'], ARRAY['Higher angle (less deep)', 'Hold for less time'], ARRAY['Keep knees aligned with toes'], 'free', 5),
  ('Diamond Push-Ups', strength_id, 4, 30, 'Advanced push-up variation targeting triceps', 'Push-up position with hands forming diamond shape. Lower and push up.', ARRAY['Maximum tricep activation', 'Chest definition', 'Core strength'], ARRAY['Perform on knees', 'Wider hand position'], ARRAY['Advanced exercise - master regular push-ups first'], 'premium', 6),
  ('Single-Leg Deadlifts', strength_id, 3, 60, 'Balance and hamstring strengthening', 'Stand on one leg, hinge at hip, reach toward floor. Return to standing.', ARRAY['Hamstring strength', 'Balance improvement', 'Hip stability'], ARRAY['Hold wall for support', 'Reduce range'], ARRAY['Keep back straight throughout'], 'premium', 7),
  ('Bulgarian Split Squats', strength_id, 4, 60, 'Advanced single-leg strength exercise', 'Rear foot elevated on chair. Lower front leg into lunge. Push back up.', ARRAY['Quad and glute strength', 'Balance training', 'Unilateral development'], ARRAY['Lower elevation', 'Hold support'], ARRAY['Master regular lunges first'], 'premium', 8),
  ('Pike Push-Ups', strength_id, 3, 30, 'Shoulder-focused push-up variation', 'Start in downward dog position. Bend elbows to lower head toward floor. Push up.', ARRAY['Shoulder strength', 'Prepares for handstands', 'Upper body power'], ARRAY['Elevate hands', 'Reduce depth'], ARRAY['Requires shoulder mobility'], 'premium', 9),
  ('Glute Bridges', strength_id, 1, 45, 'Hip and glute activation exercise', 'Lie on back, knees bent, feet flat. Lift hips to create straight line. Lower down.', ARRAY['Glute activation', 'Lower back health', 'Hip mobility'], ARRAY['Reduce range', 'Pause at top'], ARRAY['Keep core engaged'], 'free', 10),
  ('Calf Raises', strength_id, 1, 60, 'Lower leg strengthening', 'Stand on toes, raising heels as high as possible. Lower with control.', ARRAY['Calf strength', 'Ankle stability', 'Balance improvement'], ARRAY['Hold wall for support', 'Single leg variation'], ARRAY['Full range of motion for best results'], 'free', 11);
  INSERT INTO exercises (name, category_id, difficulty_level, duration_seconds, description, instructions, benefits, modifications, cautions, tier_required, display_order) VALUES
  ('Standing Forward Fold', flexibility_id, 1, 45, 'Hamstring and back stretch', 'Stand tall, hinge at hips, reach toward toes. Let upper body hang.', ARRAY['Hamstring flexibility', 'Spinal decompression', 'Calming effect'], ARRAY['Bend knees slightly', 'Hold onto shins'], ARRAY['Avoid bouncing movements'], 'free', 1),
