  ('Child''s Pose', flexibility_id, 1, 60, 'Resting yoga pose and back stretch', 'Kneel, sit back on heels, reach arms forward on floor. Relax.', ARRAY['Back relaxation', 'Hip flexibility', 'Stress reduction'], ARRAY['Place pillow under knees', 'Arms at sides instead'], ARRAY['Avoid if pregnant'], 'free', 3),
  ('Hip Flexor Stretch', flexibility_id, 2, 45, 'Stretch for front of hip', 'Kneeling lunge position. Push hips forward gently until stretch is felt.', ARRAY['Hip flexibility', 'Posture improvement', 'Lower back relief'], ARRAY['Use pad under knee', 'Reduce depth'], ARRAY['Keep back straight'], 'free', 4),
  ('Seated Butterfly', flexibility_id, 2, 60, 'Inner thigh and hip stretch', 'Sit with soles of feet together. Gently press knees toward floor.', ARRAY['Hip opening', 'Inner thigh flexibility', 'Groin stretch'], ARRAY['Sit on cushion', 'Move feet further from body'], ARRAY['Never force the stretch'], 'free', 5),
  ('Quad Stretch', flexibility_id, 1, 30, 'Standing quadriceps stretch', 'Stand on one leg, pull other foot toward glutes. Hold.', ARRAY['Quadriceps flexibility', 'Balance practice', 'Improves knee health'], ARRAY['Hold wall for support', 'Reduce intensity'], ARRAY['Keep knees together'], 'free', 6),
  ('Thread the Needle', flexibility_id, 2, 45, 'Shoulder and upper back stretch', 'On hands and knees, thread one arm under body. Hold stretch.', ARRAY['Shoulder mobility', 'Upper back release', 'Thoracic spine flexibility'], ARRAY['Use pillow under shoulder', 'Less rotation'], ARRAY['Move slowly into position'], 'premium', 7),
  ('Pigeon Pose', flexibility_id, 3, 60, 'Deep hip opener from yoga', 'Front leg bent at 90 degrees, back leg extended. Fold forward.', ARRAY['Deep hip flexibility', 'Glute stretch', 'Hip pain relief'], ARRAY['Use props under hip', 'Reclined version'], ARRAY['Advanced stretch - ease into it'], 'premium', 8),
  ('Scorpion Stretch', flexibility_id, 4, 60, 'Advanced spinal rotation stretch', 'Lie face down, rotate one leg across body, keeping shoulders down.', ARRAY['Spinal rotation', 'Hip flexibility', 'Core stretch'], ARRAY['Reduce rotation', 'Use support'], ARRAY['Advanced stretch - progress slowly'], 'premium', 9),
  ('Standing Side Bend', flexibility_id, 1, 45, 'Lateral torso stretch', 'Stand tall, reach one arm overhead and bend to the side. Hold.', ARRAY['Lateral flexibility', 'Oblique stretch', 'Posture improvement'], ARRAY['Hold onto something', 'Reduce range'], ARRAY['Keep hips stable'], 'free', 10);
  INSERT INTO exercises (name, category_id, difficulty_level, duration_seconds, description, instructions, benefits, modifications, cautions, tier_required, display_order) VALUES
  ('Plank Hold', core_id, 2, 30, 'Isometric core strengthening exercise', 'Hold push-up position with forearms on ground. Keep body straight.', ARRAY['Core stability', 'Full-body tension', 'Posture improvement'], ARRAY['Plank from knees', 'Hands instead of forearms'], ARRAY['Keep neutral spine - no sagging'], 'free', 1),
