  ('Bicycle Crunches', core_id, 3, 45, 'Dynamic ab exercise with rotation', 'Lie on back, alternate bringing elbow to opposite knee in cycling motion.', ARRAY['Oblique strength', 'Ab definition', 'Core rotation'], ARRAY['Slower pace', 'Hands behind ears not head'], ARRAY['Don''t pull on neck'], 'free', 4),
  ('Russian Twists', core_id, 3, 45, 'Seated rotational core exercise', 'Sit with feet elevated, twist torso side to side touching floor beside hips.', ARRAY['Oblique strength', 'Rotational power', 'Core endurance'], ARRAY['Keep feet on ground', 'Reduce rotation'], ARRAY['Maintain upright posture'], 'free', 5),
  ('Side Plank', core_id, 3, 30, 'Lateral core strengthening', 'Balance on one forearm and side of foot. Hold body in straight line.', ARRAY['Oblique strength', 'Lateral stability', 'Shoulder strength'], ARRAY['Bottom knee on ground', 'Reduce hold time'], ARRAY['Keep body aligned'], 'free', 6),
  ('Hollow Hold', core_id, 4, 30, 'Advanced core tension exercise', 'Lie on back, lift shoulders and legs, creating hollow body shape. Hold.', ARRAY['Deep core strength', 'Body tension', 'Gymnastics foundation'], ARRAY['Keep knees bent', 'Higher leg position'], ARRAY['Press lower back down'], 'premium', 7),
  ('Dragon Flag', core_id, 5, 30, 'Extremely advanced core exercise', 'Lying down, lift entire body keeping it straight, supported only by shoulders.', ARRAY['Maximum core strength', 'Full-body control', 'Advanced training'], ARRAY['Single leg dragon flag', 'Negative repetitions'], ARRAY['Very advanced - master prerequisites first'], 'premium', 8),
  ('L-Sit Hold', core_id, 4, 30, 'Seated isometric core exercise', 'Sit with hands beside hips, lift body and legs off ground into L-shape.', ARRAY['Hip flexor strength', 'Core power', 'Compression strength'], ARRAY['One leg at a time', 'Bent knees'], ARRAY['Requires significant strength'], 'premium', 9),
  ('Windshield Wipers', core_id, 4, 45, 'Advanced rotational core exercise', 'Hang from bar or lie down, rotate legs side to side with control.', ARRAY['Oblique strength', 'Hip mobility', 'Advanced core control'], ARRAY['Knees bent throughout', 'Smaller range'], ARRAY['Very challenging - progress slowly'], 'premium', 10),
  ('Flutter Kicks', core_id, 2, 45, 'Lower ab targeting exercise', 'Lie on back, lift legs slightly, alternate small up and down kicks.', ARRAY['Lower ab strength', 'Hip flexor endurance', 'Core stamina'], ARRAY['Higher leg position', 'Slower pace'], ARRAY['Keep lower back pressed down'], 'free', 11);
  INSERT INTO exercises (name, category_id, difficulty_level, duration_seconds, description, instructions, benefits, modifications, cautions, tier_required, display_order) VALUES
  ('Single-Leg Stand', balance_id, 1, 30, 'Basic balance challenge', 'Stand on one leg, hold position. Switch legs.', ARRAY['Balance improvement', 'Ankle strength', 'Body awareness'], ARRAY['Hold onto wall', 'Eyes open'], ARRAY['Use stable surface'], 'free', 1),
  ('Tree Pose', balance_id, 2, 45, 'Yoga balance posture', 'Stand on one leg, place other foot on inner thigh. Hands in prayer or overhead.', ARRAY['Balance training', 'Hip flexibility', 'Focus and concentration'], ARRAY['Foot on calf instead', 'Hold support'], ARRAY['Never place foot on knee'], 'free', 2),
  ('Warrior III', balance_id, 3, 30, 'Yoga balance pose with hip hinge', 'Balance on one leg, hinge forward with back leg lifting, arms extended.', ARRAY['Balance and strength', 'Hamstring engagement', 'Body alignment'], ARRAY['Hold chair', 'Hands on hips'], ARRAY['Keep hips level'], 'free', 3),
  ('Single-Leg Deadlift Reach', balance_id, 3, 45, 'Balance with forward reaching', 'Stand on one leg, reach toward floor with opposite hand. Return to standing.', ARRAY['Dynamic balance', 'Hamstring strength', 'Coordination'], ARRAY['Shallow reach', 'Hold support'], ARRAY['Controlled movement'], 'free', 4),
