  ('BOSU Ball Stand', balance_id, 3, 45, 'Unstable surface balance (simulated)', 'Imagine standing on unstable surface, engage stabilizers. Hold position.', ARRAY['Ankle stability', 'Core activation', 'Balance enhancement'], ARRAY['Both feet on ground', 'Eyes open'], ARRAY['Progress gradually'], 'premium', 6),
  ('Single-Leg Clock Reach', balance_id, 3, 60, 'Multi-directional balance challenge', 'Stand on one leg, reach other leg to clock positions (12, 3, 6, 9).', ARRAY['Multi-plane balance', 'Hip strength', 'Coordination'], ARRAY['Smaller reaches', 'Hold support'], ARRAY['Controlled movements'], 'premium', 7),
  ('Airplane Balance', balance_id, 2, 30, 'Flying balance pose', 'Balance on one leg, arms out to sides like airplane. Tilt side to side.', ARRAY['Balance fun', 'Core engagement', 'Spatial awareness'], ARRAY['Less tilting', 'Hold support'], ARRAY['Clear space around you'], 'free', 8),
  ('Tandem Stand', balance_id, 2, 30, 'Heel-to-toe balance', 'Stand with one foot directly in front of other, heel to toe. Hold.', ARRAY['Balance refinement', 'Ankle stability', 'Gait improvement'], ARRAY['Smaller gap between feet', 'Hold wall'], ARRAY['Progress from easier stances'], 'free', 9),
  ('Star Balance', balance_id, 4, 45, 'Advanced balance with reaching', 'Balance on one leg, reach arms and legs out in star shape. Hold.', ARRAY['Maximum balance challenge', 'Full-body control', 'Coordination mastery'], ARRAY['Partial star', 'Hold support'], ARRAY['Master simpler balances first'], 'premium', 10);
  INSERT INTO exercises (name, category_id, difficulty_level, duration_seconds, description, instructions, benefits, modifications, cautions, tier_required, display_order) VALUES
