  ('Squat Pulses', endurance_id, 2, 45, 'Small range squat movements', 'Hold squat position, pulse up and down in small movements.', ARRAY['Leg endurance', 'Time under tension', 'Mental toughness'], ARRAY['Shallower squat', 'Hold static instead'], ARRAY['Maintain form throughout'], 'free', 4),
  ('Plank to Downward Dog', endurance_id, 3, 60, 'Flowing movement between positions', 'Move between plank position and downward dog pose continuously.', ARRAY['Full-body endurance', 'Flexibility', 'Flow training'], ARRAY['Slow down pace', 'Hold each position longer'], ARRAY['Maintain control'], 'free', 5),
  ('Continuous Jumping', endurance_id, 3, 45, 'Sustained jumping exercise', 'Jump continuously with both feet, maintaining steady rhythm.', ARRAY['Calf endurance', 'Cardiovascular fitness', 'Explosive power'], ARRAY['Lower jump height', 'Jump rope simulation'], ARRAY['Land softly'], 'premium', 6),
